import React,{useState} from 'react';
import {useForm} from '../../shared/hooks/form-hook';
import Input from '../../shared/components/FormElements/Input';
import Button from '../../shared/components/FormElements/Button';
import {VALIDATOR_EMAIL,VALIDATOR_MINLENGTH} from '../../shared/components/utils/validators';
import '../../places/pages/NewPlace.css';
import Card from '../../shared/components/UIElements/Card';
import './Auth.css';

const Auth = () => {

	const [isLogin, setIsLogin] = useState(true);

	const [formState, inputHandler] = useForm(
		{
			email: {
				value: '',
				isValid: false,
			},
			password: {
				value: '',
				isValid: false,
			}
		},
		false
	);

	const authSubmitHandler = (event) => {
		event.preventDefault();
		console.log(formState.inputs);
	}

	const switchModeHandler = () => {

	}

	return (
		<Card className='authentication'>
			<h2>Login Required</h2>
			<form 
				className='place-form'
				onSubmit={authSubmitHandler}
			>
				<Input 
					id="email" 
					element="input"
					type="email"
					label="Email"
					validators={[VALIDATOR_EMAIL()]}
					errorText="Please enter a valid email"
					onInput= {inputHandler}
				/>
				<Input 
					id="password" 
					element="input"
					type="password"
					label="Password"
					validators={[VALIDATOR_MINLENGTH(8)]}
					errorText="Please enter a 8 characters long password"
					onInput= {inputHandler}
				/>
				<Button type='submit' disabled={!formState.isValid}>LOGIN</Button>
			</form>
			<Button onClick={switchModeHandler}>SWITCH TO SIGNUP</Button>
		</Card>
	)
}

export default Auth;
