import React, {useCallBack, useReducer} from 'react';
import './NewPlace.css';
import Input from '../../shared/components/FormElements/Input';
import {VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH} from '../../shared/components/utils/validators';

const NewPlace = () => {
	const formReducer = (state, action) => {
		switch (action.type) {
			case 'INPUT_CHANGE' :
			let formIsValid = true;
			for(const inputId in state.inputs) {
				if(inputId === action.inputId) {
					formIsValid = formIsValid && action.isValid;
				} else {
					formIsValid = formIsValid && state.inputs[inputId].isValid;
				}
			}
			return {

			}
		}
	}
	useReducer(formReducer, {
		inputs: {
			title: {
				value: '',
				isValid: false,
			},
			description: {
				value: '',
				isValid: false,
			}
		},
		isValid: false
	});

	const titleChangeHandler = useCallBack((id, value, isValid) => {},[]);
	const descriptionChangeHandler = useCallBack((id, value, isValid) => {},[]);

	return (
		<form className='place-form'>
			<Input 
				element="input" 
				type="text" 
				id="title" 
				label="Title" 
				validators={[VALIDATOR_REQUIRE()]} 
				errorText='Please enter a valid title.'
				onInput={titleChangeHandler}
			/>
			<Input 
				element="textarea" 
				type="text" 
				id="description" 
				label="Description" 
				validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(5)]} 
				errorText='Please enter a valid description.'
				onInput={descriptionChangeHandler}
			/>
		</form>
	);
}

export default NewPlace;
